import React, { Component } from "react";

class Pagination extends Component {
  render() {
    const { pageSize, onPageClick, currentPage } = this.props;
    console.log("pageSize", pageSize.length, currentPage);
    return (
      <ul className="pagination">
        <li className="page-item">
          <a
            className="page-link"
            onClick={() => {
              pageSize.length == 1 ? onPageClick(currentPage - 1) : void 0;
            }}
          >
            Previous
          </a>
        </li>
        {pageSize.map((pages) => (
          <li
            className={
              currentPage == pages.val ? "page-item active" : "page-item"
            }
          >
            <a className="page-link" onClick={() => onPageClick(pages.val)}>
              {pages.val}
            </a>
          </li>
        ))}
        <li className="page-item">
          <a
            className="page-link"
            onClick={() => {
              pageSize.length != currentPage
                ? onPageClick(currentPage + 1)
                : void 0;
            }}
          >
            Next
          </a>
        </li>
      </ul>
    );
  }
}

export default Pagination;
