import React, { Component } from "react";
import SearchElement from "./searchbar";
class Navbar extends Component {
  rightStyles = {
    float: "right",
  };

  render() {
    const {
      totalCounters,
      onCategoryClick,
      categoryClick,
      onCartClick,
      itemCategories,
      onCategoryItemClick,
    } = this.props;
    console.log("champak", categoryClick);
    return (
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand" href="javascript:void(0)">
          Amphibian
        </a>
        <div className="collapse navbar-collapse" id="navb">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                href="javascript:void(0)"
                onClick={() => onCartClick(true)}
              >
                <i className="fa fa-shopping-cart"></i> Cart {totalCounters}
              </a>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                onClick={() => onCategoryClick(!categoryClick)}
              >
                Dropdown link
              </a>
              <div
                className={
                  categoryClick ? "dropdown-menu d-block" : "dropdown-menu"
                }
              >
                {itemCategories.map((items) => (
                  <a
                    key={items.id}
                    onClick={() => onCategoryItemClick(!categoryClick)}
                    className="dropdown-item"
                    href="#"
                  >
                    {items.title}
                  </a>
                ))}
              </div>
            </li>
            {/* <li className="nav-item">
              <a className="nav-link" href="javascript:void(0)">
                Link
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link disabled" href="javascript:void(0)">
                Disabled
              </a>
            </li> */}
          </ul>
          <form className="form-inline my-2 my-lg-0">
            <input
              className="form-control mr-sm-2"
              type="text"
              placeholder="Search By Name"
              onChange={(event) => this.props.onSearchChange(event)}
            />
          </form>
        </div>
      </nav>
    );
  }
}
export default Navbar;
